	public function setFileName($name){
		$this->fileName = $name . '.docx';
	}
	
	public function getFilename(){
		return $this->fileName;
	}

	public function loadTemplate($template){
		$this->document = $this->PHPWord->loadTemplate(WWW_ROOT . 'templates/' . $template);
	}
	
	public function output(){	
		header("Content-Disposition: attachment; filename=" . $this->getFilename());     
		header("Content-Type: application/vnd.openxmlformats-officedocument.wordprocessingml.document");
		
		$tmpFile = '/tmp/' . sha1($this->getFilename() . time());
		$this->document->save($tmpFile);
		readfile($tmpFile);
		unset($tmpFile);
	}

    public function render($action = null, $layout = null, $file = null) {
		ini_set('memory_limit', '2048M');
		set_time_limit(0);
		
		App::Import('Vendor', 'DocumentGenerator.Docx', array('file' => 'PHPWord.php'));
		$this->PHPWord = new PHPWord();
		
		$renderedTemplate = parent::render($action, $layout, $file);
		$this->output = '';
		
		$this->output();
    }

